import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';


import HomeScreen from '../screens/Home/HomeScreen';
import LoginScreen from '../screens/Auth/LoginScreen';
import SignupScreen from '../screens/Auth/SignupScreen';
import SidebarScreen from '../screens/Sidebar/SidebarScreen';
import HeaderScreen from '../screens/Header/HeaderScreen';
const Stack = createNativeStackNavigator();


const AppNavigator = () => {
    return (
        <NavigationContainer>
            <Stack.Navigator>
                <Stack.Screen name="Header" component={HeaderScreen} />
                <Stack.Screen name="Home" component={HomeScreen} options={getScreenOptions('Welcome')} />
                <Stack.Screen name="Login" component={LoginScreen} options={getScreenOptions()}/>
                <Stack.Screen name="Signup" component={SignupScreen} options={getScreenOptions()} />
                <Stack.Screen name="Sidebar" component={SidebarScreen} />

            </Stack.Navigator>
        </NavigationContainer>
    );
};


const getScreenOptions = (title) => ({
    title,
    headerStyle: {
        backgroundColor: '#0b81fe',
    },
    headerTitleStyle: {
        fontSize: 20,
        fontWeight: 'bold',
        
        color: '#fff', // Customize the color
        textTransform: 'uppercase', // Convert text to uppercase
    }
});
  

export default AppNavigator;
