import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';


import HomeScreen from '../screens/Home/HomeScreen';
import LoginScreen from '../screens/Auth/LoginScreen';
import SignupScreen from '../screens/Auth/SignupScreen';
import SidebarScreen from '../components/commons/Sidebar/SidebarScreen';
import HeaderScreen from '../components/commons/Header/HeaderScreen';
import FooterScreen from '../components/commons/Footer/FooterScreen';

const Stack = createNativeStackNavigator();
const Drawer = createDrawerNavigator();
const Tab = createBottomTabNavigator();

function EmptyScreen() {
    return  <View style={styles.footer}>
    <TouchableOpacity style={styles.option}>
      <AntDesign name="home" size={24} color="white" />
      <Text style={styles.optionText}>Home</Text>
    </TouchableOpacity>
    <TouchableOpacity style={styles.option}>
      <Feather name="message-square" size={24} color="white" />
      <Text style={styles.optionText}>Chat</Text>
    </TouchableOpacity>
    <TouchableOpacity style={styles.option}>
      <FontAwesome name="user-circle" size={24} color="white" />
      <Text style={styles.optionText}>Profile</Text>
    </TouchableOpacity>
  </View>
  }

function Home() {
    return (
      <Tab.Navigator>
        <Tab.Screen name="Settings" component={EmptyScreen} />
        <Tab.Screen name="Settings34" component={EmptyScreen} />
        <Tab.Screen name="Settings1" component={EmptyScreen} />
        <Tab.Screen name="Settings2" component={EmptyScreen} />
        <Tab.Screen name="Settings3" component={EmptyScreen} />
        <Tab.Screen name="Settings35" component={EmptyScreen} />
      </Tab.Navigator>
    );
  }

function Root() {
return (
    <Drawer.Navigator useLegacyImplementation>
    <Drawer.Screen name="Home" component={Home} />
    <Drawer.Screen name="Profile" component={EmptyScreen} />
    <Stack.Screen name="Settings" component={EmptyScreen} />
    </Drawer.Navigator>
);
}


const AppNavigator = () => {
    return (
        <NavigationContainer>
            <Stack.Navigator>

            <Stack.Screen name="Sidebar1" component={Home} />


                <Stack.Screen name="Sidebar" component={SidebarScreen} />
                <Stack.Screen name="Home" component={HomeScreen} options={{ header: () => <HeaderScreen title="Home" /> }} />
                <Stack.Screen name="Signup" component={SignupScreen} options={getScreenOptions()} />
                <Stack.Screen name="Login" component={LoginScreen} options={getScreenOptions()}/>
                <Stack.Screen name="Header" component= {()=><HeaderScreen title='Header' />}   options={{ headerShown: false }} />
                <Stack.Screen name="Footer" component={FooterScreen} />
            </Stack.Navigator>
        </NavigationContainer>
    );
};



const getScreenOptions = (title) => ({
    title,
    headerStyle: {
        backgroundColor: '#0bbefe',
        borderBottomLeftRadius: 10,
        borderBottomRightRadius: 10,
        },
    headerTitleStyle: {
        fontSize: 20,
        fontWeight: 'bold',
        color: '#fff', // Customize the color
        textTransform: 'uppercase', // Convert text to uppercase
    }
});
  

export default AppNavigator;
