import {useState} from 'react'
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View,TouchableHighligh,t } from 'react-native';


export default function App() {


  const handlePress = () => {
    // Handle press event here if needed
  };


  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
      <Button  title="Press Me" style="blue"/>

      <TouchableHighlight  underlayColor="blue" activeOpacity={1}  style={styles.subcontainer}>
      <Text style={styles.text}> show the user detail </Text>
      </TouchableHighlight>




      <TouchableWithoutFeedback onPress={handlePress}>
        <View style={[styles.subcontainer, styles.hoverable]}>
          <Text style={styles.text}>Show the user detail</Text>
        </View>
      </TouchableWithoutFeedback>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  subcontainer:{
    padding: '10px',
    backgroundColor:'red',
    borderRadius:'10px'
  },
  text: {
    color: 'white',
  },
  pressed: {
    backgroundColor: 'blue', // Change the background color when pressed
  },

});
