import {useState} from 'react'
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View,TouchableOpacity } from 'react-native';

export default function App() {


  const [isHovered, setIsHovered] = useState(false);

  const handlePressIn = () => {
    setIsHovered(true);
  };

  const handlePressOut = () => {
    setIsHovered(false);
  };

  const data = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        data: [20, 45, 28, 80, 99, 43],
        color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`, // Adjust line color
        strokeWidth: 2, // Adjust line width
      },
    ],
  };

  const chartConfig = {
    backgroundGradientFrom: '#fff',
    backgroundGradientTo: '#fff',
    decimalPlaces: 0, // Number of decimal places for labels
    color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
  };

  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
      <Button  title="Press Me" style="blue"/>

      <TouchableOpacity
      style={[styles.button, isHovered && styles.buttonHovered]}
      onPressIn={handlePressIn}
      onPressOut={handlePressOut}
    >
      <Text style={[styles.buttonText, isHovered && styles.buttonTextHovered]}>Hover Me</Text>
    </TouchableOpacity>

    <LineChart
        data={data}
        width={350}
        height={220}
        chartConfig={chartConfig}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
   button: {
    backgroundColor: '#3498db',
    padding: 10,
    borderRadius: 5,
  },
  buttonHovered: {
    backgroundColor: '#2980b9',
  },
  buttonText: {
    color: 'white',
    textAlign: 'center',
  },
  buttonTextHovered: {
    color: 'yellow', // Change this to your desired text color for hover effect
  },
  // button:{
  //   color: blue,
  //   backgroundColor: '#3498db',
  //   padding: 10,
  //   borderRadius: 5,

  // },

});
