import { useState } from 'react'
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View, TouchableHighlight,ActivityIndicator } from 'react-native';
import { TextInput } from 'react-native-web';


export default function App() {

  const [isLoading, setIsLoading] = useState(false); // State for loader
  let [userData, setUserData] = useState({
    name: "",
    email: "",
    confirm: "",
    confirm_password: ""
  })

  const handleOnChange = (e) => {
    let name, value = e.target
    setUserData([...userData, name, value])
  };

  const handleSubmit = () => {

  }

  return (
    <View style={styles.container}>
      <Text>Signup Form !!</Text>
      <StatusBar style="dark-content" />

      <TextInput
        style={styles.input}
        placeholder="Name"
        value={"name"}
      // onChangeText={(e) => handleOnChange(e)}
      />
      <TextInput
        style={styles.input}
        placeholder="Email"
        value={"email"}
      // onChangeText={(e) => handleOnChange(e)}
      keyboardType="email-address"
      />

      <TextInput
        style={styles.input}
        placeholder="Password"
        value={"password"}
      onChangeText={(e) => handleOnChange(e)}
      />

      <TextInput
        style={styles.input}
        placeholder="Confirm Password"
        value={"confirm_password"}
      onChangeText={(e) => handleOnChange(e)}
      />

      {isLoading && <ActivityIndicator size="large" color="skyblue" />}

      <Button title="Submit" onPress={handleSubmit} />




      <TouchableHighlight underlayColor="blue" onPress={() => { handlePress }} style={styles.subcontainer}>
        <Text style={styles.text}> show the user detail </Text>
      </TouchableHighlight>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  subcontainer: {
    padding: '10px',
    backgroundColor: 'red',
    borderRadius: '10px'
  },
  text: {
    color: 'white',
  },
  pressed: {
    backgroundColor: 'blue', // Change the background color when pressed
  },

  input: {
    marginBottom: 10,
    padding: 10,
    borderWidth: 2,
    borderColor: '#ccc',
    borderRadius: 5,
    width: 250
  },
});
