import React from 'react';
import { TouchableOpacity, View, Text, StyleSheet } from 'react-native';
import { AntDesign, Feather, FontAwesome } from '@expo/vector-icons';

const CustomTabBarIcon = ({ focused }) => {
  return (
    <TouchableOpacity style={styles.tabBarIcon}>
      <View style={styles.footer}>
        <View style={styles.option}>
          <AntDesign name="home" size={24} color={focused ? '#0b81fe' : 'gray'} />
          <Text style={[styles.optionText, { color: focused ? '#0b81fe' : 'gray' }]}>Home</Text>
        </View>
        <View style={styles.option}>
          <Feather name="message-square" size={24} color={focused ? '#0b81fe' : 'gray'} />
          <Text style={[styles.optionText, { color: focused ? '#0b81fe' : 'gray' }]}>Chat</Text>
        </View>
        <View style={styles.option}>
          <FontAwesome name="user-circle" size={24} color={focused ? '#0b81fe' : 'gray'} />
          <Text style={[styles.optionText, { color: focused ? '#0b81fe' : 'gray' }]}>Profile</Text>
        </View>
      </View>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  tabBarIcon: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  footer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
  },
  option: {
    alignItems: 'center',
  },
  optionText: {
    fontSize: 12,
  },
});

export default CustomTabBarIcon;
