import { useState } from 'react'
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View, TouchableHighlight, ActivityIndicator } from 'react-native';
import { TextInput } from 'react-native-web';


export default function App() {

  const [isLoading, setIsLoading] = useState(false); // State for loader
  let [userData, setUserData] = useState({
    name: "",
    email: "",
    password: "",
    confirm_password: ""
  })

  const handleOnChange = (name, value) => {
    setUserData([...userData, { [name]: value }]);
  };



  const handleSubmit = () => {

  }

  console.log(userData)

  return (
    <View style={styles.container}>
      <Text>Signup Form !!</Text>
      <StatusBar style="dark-content" />

      <TextInput
        style={styles.input}
        placeholder="Name"
        value={userData.name}
        onChangeText={(text) => handleOnChange('name', text)}
      />
      <TextInput
        style={styles.input}
        placeholder="Email"
        value={userData.email}
        onChangeText={(text) => handleOnChange('email', text)}
        keyboardType="email-address"
      />

      <TextInput
        style={styles.input}
        placeholder="Password"
        value={userData.password}
        onChangeText={(text) => handleOnChange('password', text)}
      />

      <TextInput
        style={styles.input}
        placeholder="Confirm Password"
        value={userData.confirm_password}
        onChangeText={(text) => handleOnChange('name', text)}
      />

      {isLoading && <ActivityIndicator size="large" color="skyblue" />}

      <Button title="Submit" onPress={handleSubmit} />

{/* 
      <TouchableHighlight underlayColor="blue" onPress={() => { handlePress }} style={styles.subcontainer}>
        <Text style={styles.text}> show the user detail </Text>
      </TouchableHighlight> */}

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  subcontainer: {
    padding: '10px',
    backgroundColor: 'red',
    borderRadius: '10px'
  },
  text: {
    color: 'white',
  },
  pressed: {
    backgroundColor: 'blue', // Change the background color when pressed
  },

  input: {
    marginBottom: 10,
    padding: 10,
    borderWidth: 2,
    borderColor: '#ccc',
    borderRadius: 5,
    width: 250
  },
});
