import { useState } from 'react'
import { StatusBar } from 'expo-status-bar';
import {
  StyleSheet, Text, View, TextInput,
  TouchableOpacity, ActivityIndicator, Button
} from 'react-native';


export default function LoginScreen() {

  const [isLoading, setIsLoading] = useState(false); // State for loader
  let [userData, setUserData] = useState({
    email: "",
    password: "",
  })

  const handleOnChange = (name, value) => {
    setUserData(prevValues => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleSubmit = () => {
    setIsLoading(true)
  }


  return (
    <View style={styles.container}>
      <Text style={styles.containerheading}>Login Form !!</Text>
      <StatusBar style="dark-content" />

    
      <TextInput
        style={styles.input}
        placeholder="Email"
        value={userData.email}
        onChangeText={(text) => handleOnChange('email', text)}
        keyboardType="email-address"
      />

      <TextInput
        style={styles.input}
        placeholder="Password"
        value={userData.password}
        onChangeText={(text) => handleOnChange('password', text)}
      />

      {isLoading && (
        <>
          <ActivityIndicator size="large" color="green" />
          <Text style={styles.loader}>Loading...</Text>
        </>
      )}

      {!isLoading && (
        <TouchableOpacity style={styles.button} onPress={handleSubmit}>
          <Text style={styles.buttonText}>Login</Text>
        </TouchableOpacity>
      )}

      <Text style={styles.title}>Create a new account</Text>
      <Button
        title="Signup"
        onPress={() => navigation.navigate('Signup')}
        color="#007AFF" // Set the button color
      />



<Button
        title="Clear"
        color="transparent" // Set the button color to transparent
        style={styles.clearButton} // Apply custom button style
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  containerheading: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },

  input: {
    fontSize: 18,
    width: '80%',
    marginBottom: 20,
    padding: 10,
    borderWidth: 2,
    borderColor: '#ccc',
    borderRadius: 5,
  },

  buttonText: {
    color: 'white',
    fontWeight: 'bold',
    fontSize: 18,
    padding: 10
  },
  button: {
    backgroundColor: '#009933',
    paddingVertical: 12,     // Increase vertical padding to make the button taller
    paddingHorizontal: 24,   // Increase horizontal padding to make the button wider
    borderRadius: 10,        // Adjust the border radius for curved corners
  },
  title: {
    fontSize: 18,
    marginBottom: 20,
  },
  loader: {
    fontSize: 25,
  }

,

  clearButton: {
    width: 200,
    marginTop: 10,
    borderRadius: 8,
    borderWidth: 0, // Remove the button border
    borderColor: 'transparent', // Set the border color to transparent
  },
});
