import {useState} from 'react'
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View,TouchableHighlight } from 'react-native';
import { TextInput } from 'react-native-web';


export default function App() {

  let [ userData , setUserData ] = useState('')

  const handlePress = (e) => {
    let name, value = e.target
    setUserData([ ...userData, name, value])
  };



  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
      <Button  title="Press Me" style="blue"/>

      <TextInput name="name" placeholder="enter the name"></TextInput>
      <TextInput
        style={styles.input}
        placeholder="Name"
        value={"name"}
        // onChangeText={(text) => setName(text)}
      />
      <TextInput
        style={styles.input}
        placeholder="Email"
        value={"email"}
        // onChangeText={(text) => setEmail(text)}
        keyboardType="email-address"
      />

      <Button title="Submit" onPress={handleSubmit} />




      <TouchableHighlight  underlayColor="blue" onPress={() => { handlePress}} style={styles.subcontainer}>
      <Text style={styles.text}> show the user detail </Text>
      </TouchableHighlight>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  subcontainer:{
    padding: '10px',
    backgroundColor:'red',
    borderRadius:'10px'
  },
  text: {
    color: 'white',
  },
  pressed: {
    backgroundColor: 'blue', // Change the background color when pressed
  },

});
