import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { AntDesign, Feather, FontAwesome } from '@expo/vector-icons'; // Import icons from Expo's vector icons library

const FooterScreen = () => {
  return (
    <View style={styles.footer}>
      <TouchableOpacity style={styles.option}>
        <AntDesign name="home" size={24} color="white" />
        <Text style={styles.optionText}>Home</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.option}>
        <Feather name="message-square" size={24} color="white" />
        <Text style={styles.optionText}>Chat</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.option}>
        <FontAwesome name="user-circle" size={24} color="white" />
        <Text style={styles.optionText}>Profile</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  footer: {
    // flexDirection: 'row',
    // backgroundColor: '#0b81fe',
    // justifyContent: 'space-around',
    // alignItems: 'center',
    // paddingVertical: 8,


    flexDirection: 'row',
    backgroundColor: '#0b81fe',
    justifyContent: 'space-around',
    alignItems: 'center',
    paddingVertical: 8,
    position: 'absolute', // Position the footer at the bottom
    bottom: 0, // Place the footer at the bottom of the screen
    width: '100%', // Make the footer span the entire width
  },
  option: {
    alignItems: 'center',
  },
  optionText: {
    color: 'white',
    fontSize: 12,
  },
});

export default FooterScreen;
