}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  subcontainer:{
    padding: '10px',
    backgroundColor:'red',
    borderRadius:'10px'
  },
  text: {
    color: 'white',
  },
  pressed: {
    backgroundColor: 'blue', // Change the background color when pressed
  },

});
