import React from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet } from 'react-native';

function ProfileScreen() {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>Profile</Text>
        <TouchableOpacity style={styles.editButton}>
          <Text style={styles.editButtonText}>Edit</Text>
        </TouchableOpacity>
      </View>
      <Image
        source={"https://img.freepik.com/premium-vector/avatar-profile-icon_188544-4755.jpg"} // Replace with your profile picture source
        style={styles.profilePicture}
      />
      <Text style={styles.username}>John Doe</Text>
      <Text style={styles.bio}>Software Developer</Text>
      <Text style={styles.detailLabel}>Email:</Text>
      <Text style={styles.detailText}>john.doe@example.com</Text>
      <Text style={styles.detailLabel}>Location:</Text>
      <Text style={styles.detailText}>New York, USA</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  editButton: {
    backgroundColor: 'blue',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  editButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  profilePicture: {
    width: 120,
    height: 120,
    // borderRadius: 60,
    marginBottom: 10,
  },
  username: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  bio: {
    fontSize: 16,
    color: 'gray',
    marginBottom: 20,
  },
  detailLabel: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  detailText: {
    fontSize: 16,
    marginBottom: 20,
  },
});

export default ProfileScreen;
