import React from 'react';
import { TouchableOpacity, Text, View, StyleSheet } from 'react-native';
import { AntDesign, Feather, FontAwesome } from '@expo/vector-icons'; // Import icons from Expo's vector icons library

const CustomTabBarIcon = ({ routeName, isFocused, onPress }) => {
  const icons = {
    home: <AntDesign name="home" size={24} color={isFocused ? 'white' : 'gray'} />,
    chat: <Feather name="message-square" size={24} color={isFocused ? 'white' : 'gray'} />,
    profile: <FontAwesome name="user-circle" size={24} color={isFocused ? 'white' : 'gray'} />,
  };

  return (
    <TouchableOpacity style={styles.tabBarButton} onPress={onPress}>
      {icons[routeName]}
      <Text style={[styles.optionText, { color: isFocused ? 'white' : 'gray' }]}>
        {routeName.charAt(0).toUpperCase() + routeName.slice(1)}
      </Text>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  tabBarButton: {
    alignItems: 'center',
  },
  optionText: {
    fontSize: 12,
    marginTop: 4,
  },
});

export default CustomTabBarIcon;
