import {useState} from 'react'
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View,TouchableHighlight } from 'react-native';


export default function App() {

  const subcontainerStyle = [
    styles.subcontainer,
    isPressed ? styles.pressed : null,
  ];




  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
      <Button  title="Press Me" style="blue"/>

      <TouchableHighlight onPress={handlePress} style={subcontainerStyle}>
      <Text style={styles.text}> show the user detail </Text>
      </TouchableHighlight>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  subcontainer:{
    padding: '10px',
    backgroundColor:'red',
    borderRadius:'10px'
  },
  text: {
    color: 'white',
  },
  pressed: {
    backgroundColor: 'blue', // Change the background color when pressed
  },

});
