import React, { useState } from 'react';
import { View, Text, StyleSheet, FlatList, Image, TextInput,Button } from 'react-native';


const HomeScreen = (props) => {

  const initialData = [
    { id: '1', title: 'Movie 1', imageUrl: 'data:image/jpeg;base64,' },
    { id: '2', title: 'Movie 2', imageUrl: 'data:image/jpeg;base64,' },
    // Add more content data
  ];




  const [data, setData] = useState(initialData);
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (query) => {
    const filteredData = initialData.filter(
      (item) =>
        item.title.toLowerCase().includes(query.toLowerCase())
    );
    setData(filteredData);
    setSearchQuery(query);
  };

  const renderItem = ({ item }) => (
    <View style={styles.itemContainer}>
      <Image source={{ uri: item.imageUrl }} style={styles.itemImage} />
      <Text style={styles.itemTitle}>{item.title}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      {/* <Text style={styles.sectionTitle}>Recommended Movies</Text> */}
      <TextInput
        style={styles.searchInput}
        placeholder="Search..."
        value={searchQuery}
        onChangeText={handleSearch}
      />

      <FlatList
        data={data}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
        numColumns={2}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  searchInput: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    padding: 8,
    marginBottom: 16,
  },
  itemContainer: {
    flex: 1,
    alignItems: 'center',
    marginBottom: 16,
  },
  itemImage: {
    width: 150,
    height: 200,
    borderRadius: 8,
  },
  itemTitle: {
    marginTop: 8,
    fontSize: 16,
    textAlign: 'center',
  },
});

export default HomeScreen;
